void presentacion() {
    for (int f = 0; f < FILAS; f++) {
        for (int c = 0; c < COLUMNAS; c++) {

            if (mapa[f][c] == 0) setColor(0, 0); // Negro  #0C0C0C
            if (mapa[f][c] == 1) setColor(1, 0); // Azul   #0037DA
            if (mapa[f][c] == 2) setColor(2, 0); // Verde  #13A10E
            if (mapa[f][c] == 3) setColor(3, 0); // Aguamarina (Cyan)  #3A96DD
            if (mapa[f][c] == 4) setColor(4, 0); // Rojo   #C51019
            if (mapa[f][c] == 5) setColor(5, 0); // Púrpura (Morado)   #881798
            if (mapa[f][c] == 6) setColor(6, 0); // Amarillo (Marrón)  #C19C00
            if (mapa[f][c] == 7) setColor(7, 0); // Blanco / Gris claro    #CCCCCC
            if (mapa[f][c] == 8) setColor(8, 0); // Gris oscuro    #767676
            if (mapa[f][c] == 9) setColor(9, 0); // Azul claro     #3B78FF
            if (mapa[f][c] == 10) setColor(10, 0); // Verde claro  #16C60C
            if (mapa[f][c] == 11) setColor(11, 0); // Aguamarina claro #61D6D6
            if (mapa[f][c] == 12) setColor(12, 0); // Rojo claro       #E74856
            if (mapa[f][c] == 13) setColor(13, 0); // Púrpura claro    #B4009E
            if (mapa[f][c] == 14) setColor(14, 0); // Amarillo claro   #F9F1A5
            if (mapa[f][c] == 15) setColor(15, 0); // Blanco brillante # F2F2F2

            cout << (char)219;
        }
        cout << endl;
    }
}